package com.example.demo.services;import com.example.demo.models.Student;import com.example.demo.repositories.StudentRepository;import com.fasterxml.jackson.databind.ObjectMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.data.domain.Pageable;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.io.File;import java.io.IOException;import java.lang.reflect.Array;import java.util.Arrays;import java.util.List;@Servicepublic class StudentService {  private final StudentRepository studentRepository;  @Autowired  public StudentService(StudentRepository studentRepository) {    this.studentRepository = studentRepository;  }  public List<Student> getStudents(){    return studentRepository.findAll();  } public Student saveStudent(Student student){  return studentRepository.save(student); } public Page<Student> getAllStudents(int page, int size){   Pageable pageable = PageRequest.of(page, size);   return studentRepository.findAll(pageable); }}